<?php		include("db/dbconnection.php");    date_default_timezone_set('Asia/Manila');    $id = $_GET['id'];     $sql = "SELECT * FROM user_acc, hours_added WHERE user_acc.user_acc_id=hours_added.user_acc_id ORDER BY hours_added_id DESC";        $result = $conn->query($sql);        if ($result->num_rows > 0) {            $response['data'] = array();      // output data of each row            while($row = $result->fetch_array()) {            $view_intern = array();            $view_intern["ID"] = $row["hours_added_id"];            $view_intern["Name"] = $row["firstname"]." ".$row["middle_name"]." ".$row["lastname"];            if($row["deducted_penalty"] == "Deducted")            {                $view_intern["Hours"] = "-".$row["hours_added"]." hrs";            }else{                $view_intern["Hours"] = "+".$row["hours_added"]." hrs";            }	              $view_intern["Reason"] = $row["deduction_penalty_reason"];            $sql1 = "SELECT * FROM user_acc, hours_added WHERE user_acc.user_acc_id=hours_added.deducted_penalty_by AND hours_added.deducted_penalty_by = '".$row["deducted_penalty_by"]."'";            $result1 = $conn->query($sql1);            while($row1 = $result1->fetch_array()) {                $deducted_by = $row1["firstname"]." ".$row1["middle_name"]." ".$row1["lastname"];            }            $view_intern["By"] = $deducted_by;            $view_intern["Type"] = $row["deducted_penalty"];            $view_intern["Date"] = date('F j, Y', strtotime($row["date"])) ." at ".date('g:i A', strtotime($row["date"])) ; 		            array_push($response['data'], $view_intern);          	  }      echo json_encode($response);               } else {      echo json_encode(array('data'=>''));    }    $conn->close();?>