<?php	$id = $_GET["id"]; 		include("db/dbconnection.php");    $sql = "SELECT * FROM user_acc,intern_info WHERE user_acc.username = intern_info.username AND user_acc.username = '$id'";    $result = $conn->query($sql);    if ($result->num_rows > 0) {      $response['data'] = array();            while($row = $result->fetch_array()) {		  $view_user = array();		  $view_user["ID"] = $row["user_acc_id"];          $view_user["App ID"] = $row["app_id"];		  $view_user["Fname"] = $row["firstname"];		  $view_user["Mname"] = $row["middle_name"];          $view_user["Lname"] = $row["lastname"];		  $view_user["Age"] = "Not available";          $view_user["Gender"] = $row["sex"];		  $view_user["Bday"] = $row["birthdate"];		  $view_user["Province"] = $row["province"];          $view_user["City"] = $row["city"];		  $view_user["Brgy"] = $row["barangay"];          $view_user["Street"] = $row["street"];		  $view_user["Email"] = $row["username"];		  $view_user["Mobile"] = $row["mobile_no"];          $view_user["Religion"] = $row["religion"];		  $view_user["Civil"] = $row["civil_status"];          $view_user["University"] = $row["school"];		  $view_user["Company"] = $row["company"];		  $view_user["Department"] = $row["department"];          $view_user["Status"] = $row["intern_status"];          $view_user["GLink"] = $row["gdrive_link"];		  $view_user["Required"] = $row["required_hours"];          $view_user["StartD"] = $row["startdate"];		  $view_user["EndD"] = $row["estimated_end_date"];		  $view_user["StartT"] = $row["start_shift"];          $view_user["EndT"] = $row["end_shift"];          $view_user["Schedule"] = $row["schedule"];          array_push($response['data'], $view_user); 	  }      echo json_encode($response);      } else {      echo json_encode(array('data'=>''));    }    $conn->close();?>